import { HttpClient } from '@angular/common/http';
import { Component, Inject } from '@angular/core';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {

  httpClient: HttpClient 
  baseUrl: string

  data: IIncidient = {
    TP : "",
    Degradation : "",
    Description : "",
    Actions : "",
    Date : "",
  }


  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {

    this.httpClient = http
    this.baseUrl = baseUrl
  }



  onFormInput(name: string, e: any){

    let val = e.target.value
    // @ts-ignore
    this.data[name] = val
  }

  send(){

    this.httpClient.post<IIncidient>(this.baseUrl + 'incidient', this.data).subscribe(res=>{
      console.log('ok?', res)
    }, err => {
      console.log(err)
    })
  }


}



export interface IIncidient {
  TP: string
  Degradation: string
  Description: string
  Actions: string
  Date: string
}
<div class="form-container">
    <form >
        <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">ТП</label>
            <input type="text" class="form-control" (change)="onFormInput('TP', $event)"  >
        </div>

        <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Деградация</label>
            <input type="text" class="form-control" (change)="onFormInput('Degradation', $event)"  >
        </div>

        <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Описание, причина</label>
            <input type="text" class="form-control" (change)="onFormInput('Description', $event)"  >
        </div>

        <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Мероприятия</label>
            <input type="text" class="form-control" (change)="onFormInput('Actions', $event)"  >
        </div>

        <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Дата реализации</label>
            <input type="text" class="form-control" (change)="onFormInput('Date', $event)"  >
        </div>


        <button  class="btn btn-primary" (click)="send()">Отправить</button>
    </form>
</div>
